@using IgniteUI.Blazor.Controls

<div class="container vertical">
    <div class="legend-title">
        Wind Speed vs Boat Speed
    </div>
    <div class="legend">
        <IgbLegend
        Name="Legend"
        @ref="legend"
        Orientation="LegendOrientation.Horizontal">
        </IgbLegend>

    </div>

    <div class="container vertical fill">
        <IgbDataChart
        Name="chart"
        @ref="chart"
        IsHorizontalZoomEnabled="false"
        IsVerticalZoomEnabled="false"
        MarkerBrushes="white"
        MarkerOutlines="#8ce7d9 #ee5879"
        Brushes="#8ce7d9 #ee5879"
        Outlines="#8ce7d9 #ee5879">
            <IgbNumericAngleAxis
            Name="angleAxis"
            @ref="angleAxis"
            StartAngleOffset="-90"
            Interval="30"
            MinimumValue="0"
            MaximumValue="360"
            LabelFontWeight="Bold">
            </IgbNumericAngleAxis>

            <IgbNumericRadiusAxis
            Name="radiusAxis"
            @ref="radiusAxis"
            RadiusExtentScale="0.9"
            InnerRadiusExtentScale="0.1"
            Interval="25"
            MinimumValue="0"
            MaximumValue="100">
            </IgbNumericRadiusAxis>

            <IgbPolarAreaSeries
            Name="PolarAreaSeries1"
            @ref="polarAreaSeries1"
            DataSource="BoatSailingData"
            AngleAxisName="angleAxis"
            RadiusAxisName="radiusAxis"
            AngleMemberPath="Direction"
            RadiusMemberPath="WindSpeed"
            ShowDefaultTooltip="true"
            AreaFillOpacity="0.3"
            Thickness="1"
            Title="Wind Speed"
            MarkerType="MarkerType.Circle">
            </IgbPolarAreaSeries>

            <IgbPolarAreaSeries
            Name="PolarAreaSeries2"
            @ref="polarAreaSeries2"
            DataSource="BoatSailingData"
            AngleAxisName="angleAxis"
            RadiusAxisName="radiusAxis"
            AngleMemberPath="Direction"
            RadiusMemberPath="BoatSpeed"
            ShowDefaultTooltip="true"
            AreaFillOpacity="0.3"
            Thickness="1"
            Title="Boat Speed"
            MarkerType="MarkerType.Circle">
            </IgbPolarAreaSeries>

        </IgbDataChart>

    </div>
</div>

@code {


    private Action BindElements { get; set; }

    protected override async Task OnAfterRenderAsync(bool firstRender)
    {
        var legend = this.legend;
        var chart = this.chart;
        var angleAxis = this.angleAxis;
        var radiusAxis = this.radiusAxis;
        var polarAreaSeries1 = this.polarAreaSeries1;
        var polarAreaSeries2 = this.polarAreaSeries2;

        this.BindElements = () => {
            chart.Legend = this.legend;
        };
        this.BindElements();

    }

    private IgbLegend legend;
    private IgbDataChart chart;
    private IgbNumericAngleAxis angleAxis;
    private IgbNumericRadiusAxis radiusAxis;
    private IgbPolarAreaSeries polarAreaSeries1;
    private IgbPolarAreaSeries polarAreaSeries2;

    private BoatSailingData _boatSailingData = null;
    public BoatSailingData BoatSailingData
    {
        get
        {
            if (_boatSailingData == null)
            {
                _boatSailingData = new BoatSailingData();
            }
            return _boatSailingData;
        }
    }

}

using System;
using System.Collections.Generic;
public class BoatSailingDataItem
{
    public double Direction { get; set; }
    public double BoatSpeed { get; set; }
    public double WindSpeed { get; set; }
}

public class BoatSailingData
    : List<BoatSailingDataItem>
{
    public BoatSailingData()
    {
        this.Add(new BoatSailingDataItem()
        {
            Direction = 0,
            BoatSpeed = 70,
            WindSpeed = 90
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 45,
            BoatSpeed = 35,
            WindSpeed = 65
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 90,
            BoatSpeed = 25,
            WindSpeed = 45
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 135,
            BoatSpeed = 15,
            WindSpeed = 25
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 180,
            BoatSpeed = 0,
            WindSpeed = 0
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 225,
            BoatSpeed = 15,
            WindSpeed = 25
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 270,
            BoatSpeed = 25,
            WindSpeed = 45
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 315,
            BoatSpeed = 35,
            WindSpeed = 65
        });
        this.Add(new BoatSailingDataItem()
        {
            Direction = 360,
            BoatSpeed = 70,
            WindSpeed = 90
        });
    }
}

using System;
using System.Net.Http;
using System.Collections.Generic;
using System.Threading.Tasks;
using System.Text;
using Microsoft.AspNetCore.Components.WebAssembly.Hosting;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging;
using IgniteUI.Blazor.Controls; // for registering Ignite UI modules

namespace Infragistics.Samples
{
    public class Program
    {
        public static async Task Main(string[] args)
        {
            var builder = WebAssemblyHostBuilder.CreateDefault(args);
            builder.RootComponents.Add<App>("app");
            builder.Services.AddScoped(sp => new HttpClient { BaseAddress = new Uri(builder.HostEnvironment.BaseAddress) });
            // registering Ignite UI modules
            builder.Services.AddIgniteUIBlazor(
                typeof(IgbDataChartCoreModule),
                typeof(IgbDataChartPolarModule),
                typeof(IgbDataChartPolarCoreModule),
                typeof(IgbDataChartInteractivityModule),
                typeof(IgbDataChartAnnotationModule),
                typeof(IgbLegendModule)
            );
            await builder.Build().RunAsync();
        }
    }
}
