@using IgniteUI.Blazor.Controls

<div class="container vertical">
    <div class="legend-title">
        Monthly Temperature Range in LA and NYC
    </div>
    <div class="legend">
        <IgbLegend
        Name="Legend"
        @ref="legend"
        Orientation="LegendOrientation.Horizontal">
        </IgbLegend>

    </div>

    <div class="container vertical fill">
        <IgbDataChart
        Name="chart"
        @ref="chart"
        IsHorizontalZoomEnabled="false"
        IsVerticalZoomEnabled="false">
            <IgbCategoryXAxis
            Name="xAxis"
            @ref="xAxis"
            Label="Month"
            Interval="1"
            DataSource="TemperatureRangeData">
            </IgbCategoryXAxis>

            <IgbNumericYAxis
            Name="yAxis"
            @ref="yAxis"
            Title="Temperature (in Celsius)"
            TitleAngle="90"
            TitleLeftMargin="10">
            </IgbNumericYAxis>

            <IgbRangeAreaSeries
            Name="RangeAreaSeries1"
            @ref="rangeAreaSeries1"
            XAxisName="xAxis"
            YAxisName="yAxis"
            Title="Los Angeles"
            LowMemberPath="LowLA"
            HighMemberPath="HighLA"
            ShowDefaultTooltip="false"
            DataSource="TemperatureRangeData">
            </IgbRangeAreaSeries>

            <IgbRangeAreaSeries
            Name="RangeAreaSeries2"
            @ref="rangeAreaSeries2"
            XAxisName="xAxis"
            YAxisName="yAxis"
            Title="New York"
            LowMemberPath="LowNY"
            HighMemberPath="HighNY"
            ShowDefaultTooltip="false"
            DataSource="TemperatureRangeData">
            </IgbRangeAreaSeries>

            <IgbDataToolTipLayer
            Name="DataToolTipLayer"
            @ref="dataToolTipLayer">
            </IgbDataToolTipLayer>

        </IgbDataChart>

    </div>
</div>

@code {


    private Action BindElements { get; set; }

    protected override async Task OnAfterRenderAsync(bool firstRender)
    {
        var legend = this.legend;
        var chart = this.chart;
        var xAxis = this.xAxis;
        var yAxis = this.yAxis;
        var rangeAreaSeries1 = this.rangeAreaSeries1;
        var rangeAreaSeries2 = this.rangeAreaSeries2;
        var dataToolTipLayer = this.dataToolTipLayer;

        this.BindElements = () => {
            chart.Legend = this.legend;
        };
        this.BindElements();

    }

    private IgbLegend legend;
    private IgbDataChart chart;
    private IgbCategoryXAxis xAxis;
    private IgbNumericYAxis yAxis;
    private IgbRangeAreaSeries rangeAreaSeries1;
    private IgbRangeAreaSeries rangeAreaSeries2;
    private IgbDataToolTipLayer dataToolTipLayer;

    private TemperatureRangeData _temperatureRangeData = null;
    public TemperatureRangeData TemperatureRangeData
    {
        get
        {
            if (_temperatureRangeData == null)
            {
                _temperatureRangeData = new TemperatureRangeData();
            }
            return _temperatureRangeData;
        }
    }

}

using System;
using System.Net.Http;
using System.Collections.Generic;
using System.Threading.Tasks;
using System.Text;
using Microsoft.AspNetCore.Components.WebAssembly.Hosting;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging;
using IgniteUI.Blazor.Controls; // for registering Ignite UI modules

namespace Infragistics.Samples
{
    public class Program
    {
        public static async Task Main(string[] args)
        {
            var builder = WebAssemblyHostBuilder.CreateDefault(args);
            builder.RootComponents.Add<App>("app");
            builder.Services.AddScoped(sp => new HttpClient { BaseAddress = new Uri(builder.HostEnvironment.BaseAddress) });
            // registering Ignite UI modules
            builder.Services.AddIgniteUIBlazor(
                typeof(IgbDataChartCoreModule),
                typeof(IgbDataChartCategoryModule),
                typeof(IgbDataChartInteractivityModule),
                typeof(IgbDataChartAnnotationModule),
                typeof(IgbLegendModule)
            );
            await builder.Build().RunAsync();
        }
    }
}

using System;
using System.Collections.Generic;
public class TemperatureRangeDataItem
{
    public string Month { get; set; }
    public double HighNY { get; set; }
    public double LowNY { get; set; }
    public double HighLA { get; set; }
    public double LowLA { get; set; }
}

public class TemperatureRangeData
    : List<TemperatureRangeDataItem>
{
    public TemperatureRangeData()
    {
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Jan",
            HighNY = 10.6,
            LowNY = -6.6,
            HighLA = 28.3,
            LowLA = 7.8
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Feb",
            HighNY = 7.8,
            LowNY = -9.9,
            HighLA = 31.1,
            LowLA = 5.6
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Mar",
            HighNY = 12.2,
            LowNY = -3.8,
            HighLA = 27.8,
            LowLA = 8.3
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Apr",
            HighNY = 11.7,
            LowNY = 2.2,
            HighLA = 33.9,
            LowLA = 10.6
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"May",
            HighNY = 19.4,
            LowNY = 1.1,
            HighLA = 35,
            LowLA = 13.9
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Jun",
            HighNY = 23.3,
            LowNY = 10.6,
            HighLA = 36.7,
            LowLA = 16.1
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Jul",
            HighNY = 27.2,
            LowNY = 19.4,
            HighLA = 33.3,
            LowLA = 15.6
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Aug",
            HighNY = 25.6,
            LowNY = 16.7,
            HighLA = 36.7,
            LowLA = 15.6
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Sep",
            HighNY = 22.8,
            LowNY = 8.9,
            HighLA = 43.9,
            LowLA = 16.1
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Oct",
            HighNY = 17.8,
            LowNY = 0,
            HighLA = 38.3,
            LowLA = 11.1
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Nov",
            HighNY = 17.8,
            LowNY = -1,
            HighLA = 32.8,
            LowLA = 6.7
        });
        this.Add(new TemperatureRangeDataItem()
        {
            Month = @"Dec",
            HighNY = 8.3,
            LowNY = -6.6,
            HighLA = 28.9,
            LowLA = 5.6
        });
    }
}
