@using IgniteUI.Blazor.Controls

<div class="container vertical">
    <div class="container vertical">
            <IgbFinancialChart Width="100%"
                Height="100%"
                ChartType=FinancialChartType.Line
                Thickness="2"
                XAxisMode="FinancialChartXAxisMode.Ordinal"
                YAxisMode="FinancialChartYAxisMode.Numeric"
                YAxisExtent="60"
                DataSourceScript="getFCHighVolumeData"
                />
    </div>
</div>

@code {

    protected override async Task OnAfterRenderAsync(bool firstRender)
    {
        if (firstRender)
        {
            var dateStart = DateTime.Now.AddYears(-20);
            var dateEnd = DateTime.Now.Date;

            await IgniteUIBlazor.JsRuntime.InvokeVoidAsync("setFCGenerationRange", dateStart, dateEnd);
        }
    }

}

using System;
using System.Net.Http;
using System.Collections.Generic;
using System.Threading.Tasks;
using System.Text;
using Microsoft.AspNetCore.Components.WebAssembly.Hosting;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging;
using IgniteUI.Blazor.Controls; // for registering Ignite UI modules

namespace Infragistics.Samples
{
    public class Program
    {
        public static async Task Main(string[] args)
        {
            var builder = WebAssemblyHostBuilder.CreateDefault(args);
            builder.RootComponents.Add<App>("app");
            builder.Services.AddScoped(sp => new HttpClient { BaseAddress = new Uri(builder.HostEnvironment.BaseAddress) });
            // registering Ignite UI modules
            builder.Services.AddIgniteUIBlazor(
                typeof(IgbFinancialChartModule)
            );
            await builder.Build().RunAsync();
        }
    }
}

ï»¿using System;
using System.Collections.Generic;
using System.Linq;

namespace Infragistics.Samples
{
    public class StocksUtility
    {
        public static int intervalDays = 1;
        public static int intervalHours = 0;
        public static int intervalMinutes = 0;

        public static double priceStart = 200;
        public static double priceRange = 1;
        public static double volumeRange = 1000;
        public static double volumeStart = 20000000;

        public static Random rand = new Random();

        public static List<StockItem> GetStocksFrom(DateTime dateEnd, int years)
        {
            var dateStart = dateEnd.AddYears(-years);
            return GetStocksBetween(dateStart, dateEnd);
        }

        public static List<StockItem> GetStocksItems(int points)
        {
            intervalDays = 0;
            intervalHours = 1;
            intervalMinutes = 0;

            var today = DateTime.Today;
            var year = today.Year;
            var dateEnd = new DateTime(year, 11, 1);
            var dateStart = dateEnd.AddHours(-points);
            return GetStocksBetween(dateStart, dateEnd);
        }

        public static List<StockItem> GetStocksBetween(DateTime dateStart, DateTime dateEnd)
        {

            var interval = intervalDays * 24 * 60;
            interval += intervalHours * 60;
            interval += intervalMinutes;

            var time = dateStart.AddDays(0);
            var v = volumeStart;
            var o = priceStart;

            var h = o + (rand.NextDouble() * priceRange);
            var l = o - (rand.NextDouble() * priceRange);
            var c = l + (rand.NextDouble() * (h - l));

            var stock = new List<StockItem> { };
            while (time < dateEnd)
            {
                var stockItem =
                new StockItem {
                    Date = time, Open = o, High = h, Low = l, Close = c, Volume = v
                };

                stock.Add(stockItem);

                o = c + ((rand.NextDouble() - 0.5) * priceRange);
                if (o < 0)
                {
                    o = Math.Abs(o) + 2;
                }
                h = o + (rand.NextDouble() * priceRange);
                l = o - (rand.NextDouble() * priceRange);
                c = l + (rand.NextDouble() * (h - l));
                v = v + ((rand.NextDouble() - 0.5) * volumeRange);
                if (v < 0)
                {
                    v = Math.Abs(v) + 10000;
                }

                o = Math.Round(o * 100) / 100;
                h = Math.Round(h * 100) / 100;
                l = Math.Round(l * 100) / 100;
                c = Math.Round(c * 100) / 100;
                v = Math.Round(v * 100) / 100;

                time = time.AddMinutes(interval);
            }
            //setting data intent for Series Title
            //   (stock as any).__dataIntents = {
            //    close: ["SeriesTitle/Stock Prices"]
            //};

            return stock;
        }

        public static string ToShortString(double largeValue)
        {
            double roundValue;

            if (largeValue >= 1000000)
            {
                roundValue = Math.Round(largeValue / 100000) / 10;
                return roundValue + "M";
            }
            if (largeValue >= 1000)
            {
                roundValue = Math.Round(largeValue / 100) / 10;
                return roundValue + "K";
            }

            roundValue = Math.Round(largeValue);
            return roundValue + "";
        }

        public static DateTime GetYear(DateTime date)
        {
            return new DateTime(date.Year);
        }

        public static double GetQuarter(DateTime date)
        {
            var month = date.Month;
            double d = (month + 2) / 3;
            return Math.Round(d);
        }

        public static StockItem GetLastItem(List<StockItem> array)
        {
            if (array.Count == 0)
            {
                return null;
            }
            return array.LastOrDefault();
        }

        public static List<StockItem> GetNewItem(List<StockItem> array, int ?interval)
        {
            var lastItem = GetLastItem(array);

            if (interval == null)
            {
                interval = intervalDays * 24 * 60;
                interval += intervalHours * 60;
                interval += intervalMinutes;
            }

            var time = lastItem.Date.AddMinutes(interval.Value);
            double v = lastItem.Volume;
            double c = lastItem.Close;
            double h;
            double l;

            double o = c + ((rand.NextDouble() - 0.5) * priceRange);
            if (o < 0)
            {
                o = Math.Abs(o) + 2;
            }
            h = o + (rand.NextDouble() * priceRange);
            l = o - (rand.NextDouble() * priceRange);
            c = l + (rand.NextDouble() * (h - l));
            v += ((rand.NextDouble() - 0.5) * volumeRange);
            if (v < 0)
            {
                v = Math.Abs(v) + 10000;
            }

            var newValue = new List<StockItem> { new StockItem { Date = time, Open = o, High = h, Low = l, Close = c, Volume = v } };

            return newValue;
        }
    }

    public class StockItem
    {
        public DateTime Date { get; set; }
        public double Open { get; set; }
        public double High { get; set; }
        public double Low { get; set; }
        public double Close { get; set; }
        public double Volume { get; set; }
        public int Index { get; set; }
        public string Info { get; set; }
        public int Value { get; set; }

        public StockItem Clone()
        {
            var copy = new StockItem();
            copy.Date = this.Date;
            copy.Open = this.Open;
            copy.High = this.High;
            copy.Low = this.Low;
            copy.Close = this.Close;
            copy.Volume = this.Volume;
            return copy;
        }
    }

}

    var ___intervalDays = 0;
    var ___intervalHours = 1;
    var ___intervalMinutes = 0;

    var ___priceStart = 200;
    var ___priceRange = 1;
    var ___volumeRange = 1000;
    var ___volumeStart = 20000000;

    var ___dateStart;
    var ___dateEnd;
    var ___fcVolumeData;

    function setFCGenerationRange(dateStart, dateEnd) {
        ___dateStart = new Date(dateStart);
        ___dateEnd = new Date(dateEnd);
        generateFCHighVolumeData();
        var chart = document.querySelectorAll("igc-financial-chart");
        if (chart && chart.length > 0) {
            for (var  i = 0; i < chart.length; i++) {
                if (chart[i].dataSource === ___fcVolumeData) {
                    chart[i].notifyClearItems(___fcVolumeData);
                }
            }       
        }
    }
    
    function generateFCHighVolumeData() {
        ___fcVolumeData = getStocksBetween(___dateStart, ___dateEnd);
    
        return ___fcVolumeData;
    }
    
    function getFCHighVolumeData() {
        return ___fcVolumeData;
    }

    function addMinutes(date, minutes) {
        return new Date(date.getTime() + minutes*60000);
    }

    function getStocksBetween(dateStart, dateEnd)
    {
        var interval = ___intervalDays * 24 * 60;
        interval += ___intervalHours * 60;
        interval += ___intervalMinutes;

        var time = new Date();
        time.setTime(dateStart.getTime());
        var v = ___volumeStart;
        var o = ___priceStart;

        var h = o + (Math.random() * ___priceRange);
        var l = o - (Math.random() * ___priceRange);
        var c = l + (Math.random() * (h - l));

        var stock = [];
        while (time < dateEnd)
        {
            var stockItem =
            {
                Date: time, Open: o, High: h, Low: l, Close: c, Volume: v
            };

            stock.push(stockItem);

            o = c + ((Math.random() - 0.5) * ___priceRange);
            if (o < 0)
            {
                o = Math.abs(o) + 2;
            }
            h = o + (Math.random() * ___priceRange);
            l = o - (Math.random() * ___priceRange);
            c = l + (Math.random() * (h - l));
            v = v + ((Math.random() - 0.5) * ___volumeRange);
            if (v < 0)
            {
                v = Math.Abs(v) + 10000;
            }

            o = Math.round(o * 100) / 100;
            h = Math.round(h * 100) / 100;
            l = Math.round(l * 100) / 100;
            c = Math.round(c * 100) / 100;
            v = Math.round(v * 100) / 100;

            time = addMinutes(time, interval);
        }
        //setting data intent for Series Title
        //   (stock as any).__dataIntents = {
        //    close: ["SeriesTitle/Stock Prices"]
        //};

        return stock;
    }

    igRegisterScript("getFCHighVolumeData", getFCHighVolumeData, true);
